import React from 'react'
import classes from './modal.module.css';

const modal = (props) => (

    <div className={classes.modal}> 
    
    style={{transform:props.show ? 'translateY(0)':'translateY(-100vh)',
            opacity:props.show 
    }}
    
    {props.children}
       
    
    </div>
);

export default modal;
